<?phpnamespace Behamin\Support\Middleware;use Behamin\BResources\Resources\BasicResource;use Illuminate\Http\Request;use Illuminate\Http\Response;use Closure;class CheckProxyToken{    public function handle(Request $request, Closure $next)    {        if (!$request->hasHeader('X-PROXY-TOKEN') or $this->isProxyTokenValid($request)) {            return response(                new BasicResource(['error_message' => __('exception.access_denied')]),                Response::HTTP_UNAUTHORIZED            );        }        return $next($request);    }    private function isProxyTokenValid(Request $request): bool    {        return !in_array($request->header('X-PROXY-TOKEN'), config('proxy.tokens'), true);    }}