<?phpnamespace Behamin\Support\Middlewares;use Closure;use Illuminate\Http\Request;use Illuminate\Http\Response;class CheckProxyToken{    public function handle(Request $request, Closure $next)    {        if (!$request->hasHeader('X-PROXY-TOKEN') or $this->isProxyTokenValid($request->header('X-PROXY-TOKEN'))) {            abort(Response::HTTP_FORBIDDEN);        }        return $next($request);    }    private function isProxyTokenValid($proxyToken): bool    {        return !in_array($proxyToken, config('proxy.tokens'), true);    }}